mapper.select:
select * from bb_media_meta_data where
avatar = #{avatar}
and profile = #{profile}
and ownerguid = #{guid}
order by postDate desc
limit 1

select * from bb_media_meta_data where acknowledged = true and virtualGift = false and librarymedia = false and loosePic = false and ownerguid = #{guid}
and recipientDepth in #{depth}
order by postDate desc

select m.* from bb_media_meta_data m
left join bb_media_recipient r on (m.guid = r.media_guid) where r.recipientGuid in
#{recipient}
and
where
m.librarymedia = false and m.profile = false
and (
recipientDepth in
#{type,javaType=networkType,jdbcType=INTEGER}
and
ownerGuid in #{owner}
)
and recipientDepth in
#{type,javaType=networkType,jdbcType=INTEGER}
and m.postDate < #{bounds.endDate}
and m.postDate > #{bounds.startDate)
order by m.postDate desc
